import React from "react";

export default class ValidationForm extends React.Component {
  state = {
    name: "",
    password: "",
    email: "",
    nameError: "",
    passwordError: "",
    emailError: ""
  };

  validate = () => {
    let nameError = "";
    let passwordError = "";
    let emailErrorA = "";

    if (!this.state.email.includes("@")) {
      emailErrorA = "WRONG EMAIL";
    }

    if (emailErrorA) {
      this.setState({ emailError: emailErrorA });
      return false;
    }
    return true;
  };

  handleChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  handleSubmit = event => {
    event.preventDefault();
    const isValid = this.validate();
    if (isValid) {
      console.log(this.state);
    }
  };

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <div>
          <input
            name="name"
            placeholder="name"
            value={this.state.name}
            onChange={this.handleChange}
          />
        </div>
        <div
          style={{
            fontSize: 10,
            color: "red"
          }}
        >
          {this.state.nameError}
        </div>
        <div>
          <input
            name="email"
            placeholder="email"
            value={this.state.who}
            onChange={this.handleChange}
          />
        </div>
        <div
          style={{
            fontSize: 10,
            color: "red"
          }}
        >
          {this.state.emailError}
        </div>
        <div>
          <input
            type="password"
            name="password"
            placeholder="password"
            value={this.state.country}
            onChange={this.handleChange}
          />
        </div>
        <div
          style={{
            fontSize: 10,
            color: "red"
          }}
        >
          {this.state.passwordError}
        </div>
        <button type="submit">submit</button>
      </form>
    );
  }
}
