import React from "react";
import "./App.css";
import { Body, Body2 } from "./components/Body";
import { Header } from "./components/Header";
import { Counter } from "./components/Counter";
import { ImageSlider } from "./components/ImageSlider";

class App extends React.Component {
  state = {
    isVisible: true,
    whichComponent: "Image Slider"
  };

  render() {
    let slider = this.state.isVisible ? (
      <ImageSlider />
    ) : (
      <div>
        <Counter />
      </div>
    );

    if (!this.state.isVisible) {
      slider = <div>display nothing</div>;
    }

    const buttonText = this.state.isVisible ? "Hide" : "Show";

    return (
      <div className="App">
        <button
          onClick={() => {
            this.setState({ isVisible: !this.state.isVisible });
          }}
        >
          {buttonText}
        </button>
        {slider}

        {/* <Header
          title="こんにちは世界！"
          number={5}
          myFunc={(a, b) => a + b}
          myArr={[100, 200, 300]}
          myObj={{
            a: 13,
            b: "To jest b"
          }}
        />
        <Counter />
        <Body2 />
        <Body myFunc={(a, b) => a + b} text="Learn NOW!" text2="なんとかかん" /> */}
      </div>
    );
  }
}

export default App;
