import React from "react";
import "./App.css";
import { Body, Body2 } from "./components/Body";
import { Header } from "./components/Header";
import { Counter } from "./components/Counter";
import { ImageSlider } from "./components/ImageSlider";

const ButtonComponent = props => (
  <button
    onClick={() => {
      this.setState({ whichComponent: props.componentName });
    }}
  >
    {props.componentName}
  </button>
);

class App extends React.Component {
  state = {
    isVisible: true,
    whichComponent: "ImageSlider"
  };

  render() {
    if (this.state.whichComponent === "ImageSlider") {
      return (
        <div className="App">
          <ButtonComponent componentName="Counter" />
          <ImageSlider />
        </div>
      );
    } else if (this.state.whichComponent === "Counter") {
      return (
        <div className="App">
          <button
            onClick={() => {
              this.setState({ whichComponent: "Header" });
            }}
          >
            Header
          </button>
          <Counter />
        </div>
      );
    } else if (this.state.whichComponent === "Header") {
      return (
        <div className="App">
          <button
            onClick={() => {
              this.setState({ whichComponent: "ImageSlider" });
            }}
          >
            ImageSlider
          </button>
          <Header />
        </div>
      );
    }
  }
}

export default App;
