import React from "react";
import "./App.css";
import { Body, Body2 } from "./components/Body";
import { Header } from "./components/Header";
import { Counter } from "./components/Counter";
import { ImageSlider } from "./components/ImageSlider";

class App extends React.Component {
  state = {
    isVisible: true,
    whichComponent: "Image Slider"
  };

  render() {
    if (this.state.whichComponent === "Image Slider") {
      return (
        <div className="App">
          <button
            onClick={() => {
              this.setState({ whichComponent: "Counter" });
            }}
          >
            Counter
          </button>
          <ImageSlider />
        </div>
      );
    } else if (this.state.whichComponent === "Counter") {
      return (
        <div className="App">
          <button
            onClick={() => {
              this.setState({ whichComponent: "Header" });
            }}
          >
            Header
          </button>
          <Counter />
        </div>
      );
    } else if (this.state.whichComponent === "Header") {
      return (
        <div className="App">
          <Counter />
          <button
            onClick={() => {
              this.setState({ whichComponent: "Image Slider" });
            }}
          >
            Image Slider
          </button>
        </div>
      );
    }
  }
}

export default App;
