import React from "react";

export default class MyForm extends React.Component {
  state = {
    name: "xxx",
    country: "",
    isOn: true,
    who: "On"
  };

  handleChange = (event, fieldName) => {
    console.log(fieldName);
    this.setState({ name: event.target.value });
  };

  handleCountryChange = event => {
    this.setState({ country: event.target.value });
  };

  handleCheck = event => {
    this.setState({ isOn: event.target.checked });
  };

  handleSelect = event => {
    this.setState({ who: event.target.value });
  };

  handleSubmit = event => {
    console.log(this.state);
  };

  render() {
    return (
      <div>
        <input
          value={this.state.name}
          onChange={event => this.handleChange(event, "name")}
        />
        <textarea
          value={this.state.country}
          onChange={event => this.handleChange(event, "country")}
        />
        <input
          type="checkbox"
          checked={this.state.isOn}
          onChange={event => this.handleChange(event, "isOn")}
        />
        <div>
          <select
            value={this.state.who}
            onChange={event => this.handleChange(event, "who")}
          >
            <option>Ja</option>
            <option>Ty</option>
            <option>On</option>
          </select>
        </div>
        <button onClick={this.handleSubmit}>submit</button>
      </div>
    );
  }
}
