import React from "react";
import "./App.css";
import MyForm from "./components/MyForm";

class App extends React.Component {
  state = {
    isVisible: true
  };

  render() {
    return (
      <div className="App">
        <button
          onClick={() => {
            this.setState({ isVisible: !this.state.isVisible });
          }}
        >
          toggle counter
        </button>

        <div className={this.state.isVisible ? "visible" : "hidden"}>
          <Counter />
        </div>
      </div>
    );
  }
}

export default App;
